mapper.select:
select
  id, `name`, source_id, source_table_name, source_table_cols, target_id, target_table_name, target_table_cols, create_by
from data_gateway.scheme


        and id = #{id}


        and `name` = #{name}


        and source_id = #{sourceId}


        and source_table_name = #{sourceTableName}


        and source_table_cols = #{sourceTableCols}


        and target_id = #{targetId}


        and target_table_name = #{targetTableName}


        and target_table_cols = #{targetTableCols}


        and create_by = #{createBy}
